/*
** to compile run separately:
** clang++ -Wall -Werror -Wextra -std=c++98 client.cpp -o client
** clang++ -Wall -Werror -Wextra -std=c++98 server.cpp -o server
** to run program execute server then client in different terminals
*/

#include <iostream>
#include <sys/socket.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <string>
#define PORT 8080 
   
int main() { 
	int sock = 0;
	size_t	sent = 0;
	struct sockaddr_in serv_addr;
	const char* hello = "POST /wordpress HTTP/1.1\r\nAccept: */*\r\nAccept-Encoding: gzip\r\nAccept-Language: uk,en-US;q=0.8,en;q=0.5,ru;q=0.3\r\nCache-Control: no-cache\r\nConnection: Keep-Alive\r\nHost: httpbin.org\r\nContent-Length: 16743\r\nUser-Agent: Mozilla/5.0 (Windows NT 10.0; WOW64; Trident/7.0; rv:11.0) like Gecko\r\ncontent-type: application/json\r\n\r\\r\n\r\n";
	char buffer[1024] = {0};

	if ((sock = socket(AF_INET, SOCK_STREAM, 0)) < 0) {
		std::cerr << "\n Socket creation error \n";
		return (EXIT_FAILURE); 
    }

	serv_addr.sin_family = AF_INET; 
	serv_addr.sin_port = htons(PORT);

	// Convert IPv4 and IPv6 addresses from text to binary form 
	if((serv_addr.sin_addr.s_addr = inet_addr("127.0.0.1"))<=0) {
		std::cerr << "\nInvalid address/ Address not supported \n";
		return (EXIT_FAILURE); 
    }

	if (connect(sock, (struct sockaddr *)&serv_addr, sizeof(serv_addr)) < 0) {
		std::cerr << "\nConnection Failed \n";
		return (EXIT_FAILURE);
    }

	if ((sent = send(sock, hello, strlen(hello), 0)) != strlen(hello))
		std::cerr << "Not all msg sent\n";
	std::cout << "Hello message sent\n";
	read(sock, buffer, 1024);
	std::cout << buffer << std::endl;
	return (EXIT_SUCCESS); 
}
